import React from 'react';
import './WeatherDiv.css';



const WeatherDiv = ({weatherData}) =>{
    const icon = {
        link: "http://openweathermap.org/img/w/" + weatherData.weather[0].icon + ".png"
    }
    const temperature = weatherData.main.temp.slice(0,-3)

    return (
        <div>
            <h1 className="City">{weatherData.name}</h1>
            <img className="WeatherImage" src={icon.link} alt="Weather"/>
            <p className="Description">{weatherData.weather[0].description}</p>
            <p className="Description">{temperature} Â°C</p>
        </div>
    )

}
export default WeatherDiv
