import React, { Component } from 'react';
import './App.css';

class App extends Component {
  state = {
    weatherData : [],
    isLoading: true
  }

  getWeather = () => {
    fetch('http://api.openweathermap.org/data/2.5/weather?q=Paris&lang=fr&units=metric&appid=e8fc88dd5f1edd7f7ff6a9a5be06bd83')
    .then(res => res.json())
    .then(res => this.setState({weatherData: res}))
    .then(console.log(this.state.weatherData))
  }

  componentDidMount() {
    this.getWeather()
    }
  render() {
    return <div>{this.state.weatherData.name}  </div>
  }
}

export default App;
